// Filtrar solo las redes que tiene el comercio
$_redesActivas = [];
foreach ($_redes as $key => $info) {
    if (!empty($comercio[$key])) {
        // Instagram: usar bridge page para resolver deep linking en Android
        if ($key === 'instagram') {
            $igUser = '';
            $igReserved = ['stories', 'reels', 'p', 'explore', 'tv', 'accounts', 'direct', 'about'];
            if (preg_match('#instagram\.com/(?:(?:' . implode('|', $igReserved) . ')/)?([a-zA-Z0-9_.]+)/?#', $comercio[$key], $igMatch)) {
                $igUser = $igMatch[1];
            }
            if ($igUser && !in_array($igUser, $igReserved)) {
                $comercio[$key] = url('/ig.php?u=' . $igUser);
            }
        }
        $_redesActivas[$key] = $info;
    }
}

if (empty($_redesActivas)) {
    return;
}
?>
<div class="comercio-redes">
    <h4 class="comercio-redes__title">Redes sociales</h4>
    <div class="comercio-redes__list">
        <?php foreach ($_redesActivas as $key => $info): ?>
            <a href="<?= e($comercio[$key]) ?>"
               class="comercio-redes__link"
               style="--red-color: <?= $info['color'] ?>"
               target="_blank"
               rel="noopener noreferrer"
               title="<?= e($info['label']) ?>">
                <span class="comercio-redes__icon"><?= $info['icon'] ?></span>
                <span class="comercio-redes__label"><?= e($info['label']) ?></span>
            </a>
        <?php endforeach; ?>
    </div>
</div>
